require(grDevices)
library(ggplot2)

readData = function (file) {
    read.table(file, header = F, sep = " ", col.names = c("tick", "duration", "heap", "total"))
}

png(
  '/tmp/graph.png',
  width = 5,
  height = 5,
  units = 'in',
  res = 1200,
  pointsize = 4
)

ggplot() +
geom_line(aes(tick, duration, colour="Performance 1"), readData("/tmp/data1")) +
geom_line(aes(tick, duration, colour="Performance 2"), readData("/tmp/data2"))

dev.off()

